combobox.OnLoad:
variable index:item cargo
EVEWindow[Inventory].ChildWindow[${Me.ShipID},ShipCargo]:GetItems[cargo]
cargo:RemoveByQuery[${LavishScript.CreateQuery[Category =- "Charge"]}, FALSE]
variable iterator c
cargo:GetIterator[c]
if ${c:First(exists)}
	do
	{
		if !${This.ItemByText[${c.Value.Type}](exists)}
		{
			This:AddItem[${c.Value.Type}]
		}
	}
	while ${c:Next(exists)}
if ${Me.Station(exists)}
{
	EVEWindow[Inventory].ChildWindow[${Me.Station.ID},StationItems]:GetItems[cargo]
	cargo:RemoveByQuery[${LavishScript.CreateQuery[Category =- "Charge"]}, FALSE]
	if ${c:First(exists)}
		do
		{
			if !${This.ItemByText[${c.Value.Type}](exists)}
			{
				This:AddItem[${c.Value.Type}]
			}
		}
		while ${c:Next(exists)}
}
if ${Script[Tehbot].VariableScope.Mining.Config.WhatMiningCrystal.NotNULLOrEmpty}
{
	if !${This.ItemByText[${Script[Tehbot].VariableScope.Mining.Config.WhatMiningCrystal}](exists)}
		This:AddItem[${Script[Tehbot].VariableScope.Mining.Config.WhatMiningCrystal}]
}
This.ItemByText[${Script[Tehbot].VariableScope.Mining.Config.WhatMiningCrystal}]:Select
This:SetZOrder[movetop]
if !${Script[Tehbot].VariableScope.Mining.Config.UseMiningCrystals}
{
	This:Hide
}

